import React, { useState, useEffect } from "react";
import * as Style from "./styledComponents/PersonalInstruction";
import Safe from "../assets/images/DeathModuleNew/Sandglass&Bitcoin-1.png";
import SeeMore from "../assets/images/DeathModuleNew/more_horiz.png";
import menuIcon from "../assets/images/DeathModuleNew/Menu.png";
import fileIcon from "../assets/images/DeathModuleNew/Icons.png";
import DEATH_API from "../apis/death.api";
import { Modal } from "antd";
import Table from "./styledComponents/Table/Table";
import List from "./styledComponents/Table/List";

export const EmailandText = () => {
  const {emails, setEmails} = useState();

  const emailData = [
    {
      To: "test@gmail.com",
      Subject: "New Testing",
      "Email Status": "Sent",
      Action: (
        <div>
          {" "}
          <img
            style={{ cursor: "pointer", marginRight: "1rem" }}
            src={menuIcon}
            alt="menuIcon"
          ></img>{" "}
        </div>
      ),
    },
    {
      To: "test@gmail.com",
      Subject: "New Testing",
      "Email Status": "Sent",
      Action: (
        <div>
          {" "}
          <img
            style={{ cursor: "pointer", marginRight: "1rem" }}
            src={menuIcon}
            alt="menuIcon"
          ></img>{" "}
        </div>
      ),
    },
    {
      To: "test@gmail.com",
      Subject: "New Testing",
      "Email Status": "Sent",
      Action: (
        <div>
          {" "}
          <img
            style={{ cursor: "pointer", marginRight: "1rem" }}
            src={menuIcon}
            alt="menuIcon"
          ></img>{" "}
        </div>
      ),
    },
  ];
  const textData = [
    {
      To: "test@gmail.com",
      Text: "New Testing",
      "Text Status": "Sent",
      "Phone Number": "04322323423",
    },
    {
      To: "test@gmail.com",
      Text: "New Testing",
      "Text Status": "Sent",
      "Phone Number": "04322323423",
    },
    {
      To: "test@gmail.com",
      Text: "New Testing",
      "Text Status": "Sent",
      "Phone Number": "04322323423",
    },
  ];
  const emailStats = [
    {
      "No. of Email Sent": "01",
      "No. of Email Rejected": "02",
      "Email of Rejected": "test@gmail.com",
      "Email Opened": "01",
    },
    {
      "No. of Email Sent": "01",
      "No. of Email Rejected": "02",
      "Email of Rejected": "test@gmail.com",
      "Email Opened": "01",
    },
    {
      "No. of Email Sent": "01",
      "No. of Email Rejected": "02",
      "Email of Rejected": "test@gmail.com",
      "Email Opened": "01",
    },
  ];
  const [showModal, setShowModal] = useState(false);
  const handelCloseModal = () => {
    setShowModal(false);
  };
  const [showTextModal, setTextShowModal] = useState(false);
  const handelCloseTextModal = () => {
    setTextShowModal(false);
  };
  const getEmail = async () => {
        try {
      // if (
      //   this.props.checklistObject.emailToSendForm &&
      //   this.props.checklistObject.emailToSendForm.hasOwnProperty(
      //     "email_to_send"
      //   )
      // )
        this.setState({
          email_to_send:
            this.props.checklistObject.emailToSendForm.email_to_send,
        });
      (async () => {
        this.props.handleLoader();
        const ID = localStorage.getItem("accessId");
        let Role =
          localStorage.getItem("role") &&
          JSON.parse(localStorage.getItem("role"));
        if (Role == "protrustee") {
          const data = await DEATH_API.getFuneralInfo(ID);
          console.log("data funeral info", data);
          if (data && data.data) {
            this.setState({
              FuneralData: data.data,
            });
          }
        }
        const data = await DEATH_API.fetchEmail(ID);
        const dataText = await DEATH_API.fetchText(ID);
        if (data && data.data) {
          this.setState({
            email_to_send: data.data.map((item) => {
              return {
                ...item,
                To: item.to,
                CC: item.cc,
                Subject: item.subject,
                TemplateNickName: item.email_template.templateName,
                Template: item.email_template.template,
                Templates: item.email_template.templateName,
                sendStatus: item.sendStatus,
                emailTemplateId: item.email_template.id,
              };
            }),
          });

          data.data.map((item) => {
            if (item.sendStatus === false) {
              this.setState({ emailButton: item.sendStatus });
            }
          });
        }
        const datatemp = await DEATH_API.getTemplate();
        // console.log("datatempdatatempdatatemp>>",datatemp);
        if (datatemp && datatemp.data) {
          this.setState({
            templateValue: datatemp.data.map((item) => {
              return {
                ...item,
                id: item.id,
                name: item.templateName,
              };
            }),
          });
        }
        // console.log("data.templates",data.templates)
        if (data && data.templates) {
          this.setState({
            templatesName: data.templates.map((items) => items.templateName),
          });
        }
        if (dataText && dataText.data) {
          this.setState({
            texts: dataText.data.map((item) => {
              return {
                ...item,
                TextTo: item.to,
                Phone: item.phoneNumber,
                Subject: item.subject,
                Relationship: item.relationship,
                TextTemplateNickname: item.templateName,
                TextMessage: item.message,
                Template: item.text_template.template,
                textTemplateId: item.text_template.id,
                TextTemplates: item.text_template.templateName,
              };
            }),
          });
          dataText.data.map((item) => {
            if (item.sendStatus === false) {
              this.setState({ textButoon: item.sendStatus });
              console.log("text status", item.sendStatus);
            }
          });
        }

        const datatemp1 = await DEATH_API.getTextTemplate();
        // console.log("datatempdatatempdatatemp>>",datatemp);
        if (datatemp1 && datatemp1.data) {
          this.setState({
            templateValueText: datatemp1.data.map((item) => {
              return {
                ...item,
                id: item.id,
                name: item.templateName,
              };
            }),
          });
        }

        const dataTable = await DEATH_API.getEmailCounts();
        console.log("data in death status", dataTable);
        if (dataTable && dataTable.countEmailLogs) {
          console.log("here in email table fetching", dataTable.countEmailLogs);

          const statusCountObject = {};
          dataTable.countEmailLogs.forEach((item) => {
            statusCountObject[item.status] = item.count;
          });
          let wrapper = [statusCountObject];
          console.log("wrapper", wrapper);
          this.setState({
            emailLogsCount: wrapper,
          });
          // let tableRow = dataTable.countEmailLogs.map((item, index) => (
          //   item['status'] = item.status,
          //   item['count'] = item.count
          // ))
          // console.log("tableRow", tableRow);
        }

        this.props.handleLoader();
      })();
      this.props.handleChecklistObject(
        this.props.currentForm,
        this.state.email_to_send
      );
      this.props.handleChecklistObject(
        this.props.currentForm,
        this.state.texts
      );
    } catch (error) {
      // this.props.handleLoader();
      console.group(error);
    }
  }
  useEffect(() => {
    getEmail();
  }, []);
  const cols = [
    {
      title: "Amount",
      dataIndex: "Name",
      //   sorter: (a, b) =>
      //     a.firstName > b.firstName ? 1 : a.firstName === b.firstName ? 0 : -1,
    //   render: (record) => (record ? record : "N/A"),
      render: (record, index) => {
        // console.log("index...>>>",record.idx);
        return (
          <div>
            {" "}
            {record ?
            <>
            <img
              style={{ cursor: "pointer", marginRight: "8px" }}
              src={TitleImg}
              alt="TitleImg"
            ></img>  {record}
            </>
            :
            "N/A"
            }
          </div>
        );
      },
    },
    {
      title: "Payee Name",
      dataIndex: "location",
      //   sorter: (a, b) => a["lastName"] > b["lastName"],
      render: (record) => (record ? record : "N/A"),
    },
    {
      title: "Category",
      dataIndex: "address",
      // sorter: (a, b) => a["Phone"] > b["Phone"],
      render: (record) => (record ? record : "N/A"),
    },
    {
      title: "Frequency",
      dataIndex: "Paidfor",
      // sorter: (a, b) => a["Email"] > b["Email"],
      render: (record) => (record ? record : "N/A"),
    },

    {
      title: "Date of Paid",
      dataIndex: "date",
      //   sorter: (a, b) => a["secondPasswordGranted"] > b["secondPasswordGranted"],
      render: (record) => (record ? record : "N/A"),
    },
    {
      title: "Notes",
      dataIndex: "note",
      // key: "account",
      // sorter: (a, b) => a["Notes"] > b["Notes"],
      // sorter: (a, b) =>
      //   a["Notes"] > b["Notes"] ? 1 : a["Notes"] === b["Notes"] ? 0 : -1,
      render: (record) => (record ? record : "N/A"),
    },

    {
      title: "Action",
      render: (record, index) => {
        // console.log("index...>>>",record.idx);
        return (
          <div>
            {" "}
            <img
              style={{ cursor: "pointer", marginRight: "1rem" }}
              src={menuIcon}
              alt="menuIcon"
            ></img>{" "}
          </div>
        );
      },
    },
  ];
  return (
    <div>
      <Modal closable={false} visible={showModal} centered footer={false}>
        <div>
          <h3 style={{ color: "#4d5e80", marginBottom: "10px" }}>
            {" "}
            Add New Email
          </h3>
          <Style.Input placeholder="To" name="To" />{" "}
          <Style.Input placeholder="Template" name="Template" />
          <Style.Input placeholder="Subject" name="Subject" />
          <Style.Input placeholder="Body of Email" name="Body of Email" />
          <Style.WrapperButtons>
            <Style.CancelButton
              id="stop"
              onClick={() => {
                handelCloseModal();
              }}
            >
              Cancel
            </Style.CancelButton>
            <Style.SaveButton id="stop" onClick={handelCloseModal}>
              Save
            </Style.SaveButton>
          </Style.WrapperButtons>
        </div>
      </Modal>
      <Modal closable={false} visible={showTextModal} centered footer={false}>
        <div>
          <h3 style={{ color: "#4d5e80", marginBottom: "10px" }}>
            {" "}
            Add New Text
          </h3>
          <Style.Input placeholder="To" name="To" />{" "}
          <Style.Input placeholder="Template" name="Template" />
          <Style.Input placeholder="Phone" name="Phone" />
          <Style.Input placeholder="Body of Text" name="Body of Text" />
          <Style.WrapperButtons>
            <Style.CancelButton
              id="stop"
              onClick={() => {
                handelCloseTextModal();
              }}
            >
              Cancel
            </Style.CancelButton>
            <Style.SaveButton id="stop" onClick={handelCloseTextModal}>
              Save
            </Style.SaveButton>
          </Style.WrapperButtons>
        </div>
      </Modal>
      <Style.MainHeading>
        <Style.HeaderImg src={Safe} alt="Safe"></Style.HeaderImg>
        Email & Text to Send
      </Style.MainHeading>

      <Style.HealthCard>
        <Style.HealthCardHeader>
          <Style.Title style={{}}>Emails</Style.Title>
          <div>
            <Style.Button
              onClick={() => {
                setShowModal(true);
              }}
            >
              +
            </Style.Button>
          </div>
        </Style.HealthCardHeader>
        <List
        data={emailData} name={"email"} 
          // loading={this.state.report_rows ? false : true}
          // loading={
          //   this.state.contact_list ? this.state.contact_list <= 0 : false
          // }
          cols={cols}
          rows={data}
          // onChange={this.handleChange}
        ></List>
        {/* <Table data={emailData} name={"email"} /> */}
      </Style.HealthCard>
      <Style.HealthCard>
        <Style.HealthCardHeader>
          <Style.Title style={{}}>Texts</Style.Title>
          <div>
            <Style.Button
              onClick={() => {
                setTextShowModal(true);
              }}
            >
              +
            </Style.Button>
          </div>
        </Style.HealthCardHeader>
        <Table data={textData} name={"text"} />
      </Style.HealthCard>
      <Style.HealthCard>
        <Style.HealthCardHeader>
          <Style.Title style={{}}>Email Stats</Style.Title>
        </Style.HealthCardHeader>
        <Table data={emailStats} name={"stats"} />
      </Style.HealthCard>
    </div>
  );
};
